"use strict";

var settings = require("./settings");
var setsCtrl = require("./sets/controller");

var FORMATTING = [{ name: settings.id, className: "fa fa-smile-o", title: "[[" + settings.iD + ":composer.title]]" }];

var __push = Array.prototype.push;

/*===================================================== Exports  =====================================================*/

exports.formatting = function (data) {
  if (setsCtrl.getActiveIds().length) {
    __push.apply(data, FORMATTING);
  }
};
