"use strict";

var setsCtrl = require("./sets/controller");

/*===================================================== Exports  =====================================================*/

exports.parse = prependStylesToEmail;

/*==================================================== Functions  ====================================================*/

function prependStylesToEmail(email, cb) {
  var style = setsCtrl.styles.email;
  if (style) {
    email.html = "<style type=\"text/css\">" + style + "</style>\n" + email.html;
  }
  cb(null, email);
}
